INSERT INTO public.posts (id, title, content, post_type, author_id, community_id, tags, upvotes, comment_count, is_pinned) VALUES
('p0000001-1111-1111-1111-111111111111', 'Best Indian restaurants in Bay Area?', 'Hey everyone! I''m new to the Bay Area and looking for authentic Indian restaurants. Any recommendations for good South Indian food? Especially looking for places with great dosas and sambar!', 'question', '11111111-1111-1111-1111-111111111111', (SELECT id FROM communities WHERE name = 'San Francisco Bay Area Indian Community'), ARRAY['food', 'restaurants', 'recommendations'], 23, 8, false),
('p0000002-2222-2222-2222-222222222222', 'Tech Career Growth Discussion', 'Starting a thread about career growth in tech. What are the best strategies for moving from individual contributor to management roles? Would love to hear experiences from fellow Indian professionals.', 'discussion', '22222222-2222-2222-2222-222222222222', (SELECT id FROM communities WHERE name = 'Seattle Indian Professionals'), ARRAY['career', 'tech', 'management'], 45, 12, true),
('p0000003-3333-3333-3333-333333333333', 'Free Health Checkup Camp - This Weekend', 'Our community is organizing a free health checkup camp this weekend at the community center. We''ll have doctors volunteering for basic screenings. Please spread the word!', 'announcement', '33333333-3333-3333-3333-333333333333', (SELECT id FROM communities WHERE name = 'Austin Indian Association'), ARRAY['health', 'community', 'volunteer'], 67, 5, true),
('p0000004-4444-4444-4444-444444444444', 'Investment Guide for H1B Holders', 'Comprehensive guide on investment options available for H1B visa holders. Covering 401k, IRA, taxable accounts, and tips for international investing. Let me know if you have questions!', 'resource', '44444444-4444-4444-4444-444444444444', (SELECT id FROM communities WHERE name = 'New York Indian Chamber of Commerce'), ARRAY['finance', 'investing', 'visa', 'guide'], 89, 15, false),
('p0000005-5555-5555-5555-555555555555', 'Diwali Celebration Planning', 'Who''s excited for our upcoming Diwali celebration? We''re still looking for volunteers to help with decorations and food coordination. Also, if anyone knows good Bollywood DJs, please comment below!', 'discussion', '55555555-5555-5555-5555-555555555555', (SELECT id FROM communities WHERE name = 'Chicago Desi Network'), ARRAY['diwali', 'celebration', 'volunteers'], 34, 18, false);
INSERT INTO public.comments (id, post_id, author_id, content, upvotes, parent_comment_id) VALUES
('c0000001-1111-1111-1111-111111111111', 'p0000001-1111-1111-1111-111111111111', '22222222-2222-2222-2222-222222222222', 'I highly recommend Udupi Palace in Sunnyvale! Their dosas are amazing and the sambar is authentic.', 12, null),
('c0000002-2222-2222-2222-222222222222', 'p0000001-1111-1111-1111-111111111111', '33333333-3333-3333-3333-333333333333', 'Also try Saravana Bhavan - they have multiple locations and consistently good food.', 8, null),
('c0000003-3333-3333-3333-333333333333', 'p0000002-2222-2222-2222-222222222222', '44444444-4444-4444-4444-444444444444', 'Great topic! I transitioned to management 3 years ago. Key advice: start mentoring junior engineers and take on cross-team projects.', 15, null),
('c0000004-4444-4444-4444-444444444444', 'p0000002-2222-2222-2222-222222222222', '55555555-5555-5555-5555-555555555555', 'Communication skills are crucial. Consider taking leadership courses or getting an MBA part-time.', 9, null),
('c0000005-5555-5555-5555-555555555555', 'p0000003-3333-3333-3333-333333333333', '11111111-1111-1111-1111-111111111111', 'This is wonderful! Will there be any specialists for eye checkups? My elderly parents could really benefit from this.', 6, null);